import{api,setCsrfToken}from"../api.js";export function registerLogout(n){n("/logout",(async n=>{n.innerHTML='\n      <div class="auth-container">\n        <div class="auth-wrapper">\n          <div class="card auth-card">\n            <div class="auth-header">\n              <h1 class="auth-logo">S-Store</h1>\n              <p class="text-muted">Logging out...</p>\n            </div>\n            <div style="padding: 2rem; text-align: center;">\n              <div class="spinner"></div>\n            </div>\n          </div>\n        </div>\n      </div>';try{const n=await api("/auth/logout",{method:"POST"});n?.csrfToken&&setCsrfToken(n.csrfToken)}catch(n){}finally{location.hash="/login"}}))}